import { useState, useEffect } from "react";
import { motion } from "framer-motion";

/* -------------------- tiny helpers -------------------- */
const fadeUp = { hidden: { opacity: 0, y: 24 }, show: { opacity: 1, y: 0, transition: { duration: 0.6 } } };
const stagger = { show: { transition: { staggerChildren: 0.12 } } };

export default function Dashboard() {
  const [activeTab, setActiveTab] = useState("Dashboard");
  const [contentType, setContentType] = useState("Social Media Post");
  const [prompt, setPrompt] = useState("");
  const [generatedContent, setGeneratedContent] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => { document.title = "Dashboard"; }, []);

  const handleGenerate = async () => {
    if (!prompt) return alert("Enter a prompt first!");
    setLoading(true);
    try {
      const res = await fetch("http://localhost:8000/api/generate_content", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt }),
      });
      const data = await res.json();
      setGeneratedContent(data.answer || data.error);
    } catch (err) {
      alert("Failed to generate content");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-[#0B1020] text-white min-h-screen">
      {/* ===== DASHBOARD HERO ===== */}
      <section className="max-w-7xl mx-auto px-6 py-20">
        <motion.div variants={stagger} initial="hidden" whileInView="show" viewport={{ once: true, amount: 0.3 }} className="grid md:grid-cols-2 gap-10 items-center">
          <motion.div variants={fadeUp} className="space-y-6">
            <h1 className="text-4xl font-extrabold md:text-6xl">
              Generate <span className="bg-gradient-to-r from-fuchsia-400 via-sky-300 to-violet-300 bg-clip-text text-transparent">AI-powered content</span> instantly
            </h1>
            <p className="text-white/70 md:text-lg">
              Create posts, emails, blog articles, ad copy, and captions tailored for any platform in seconds.
            </p>
          </motion.div>

          <motion.div variants={fadeUp} className="relative">
            <div className="relative rounded-3xl border border-white/10 bg-gradient-to-tr from-white/10 to-white/5 p-5 backdrop-blur-xl shadow-2xl">
              <div className="rounded-2xl bg-[#0B1020]/60 border border-white/10 p-4">
                <p className="text-sm text-white/80">
                  {generatedContent || "Your AI-generated content will appear here!"}
                </p>
              </div>
            </div>
          </motion.div>
        </motion.div>
      </section>

      {/* ===== CONTENT GENERATOR ===== */}
      <section className="border-t border-white/10 bg-[#0C1226]">
        <div className="max-w-3xl mx-auto px-6 py-20">
          <motion.div initial={{ opacity: 0, y: 10 }} whileInView={{ opacity: 1, y: 0 }} viewport={{ once: true }} className="rounded-3xl border border-white/10 bg-white/5 p-6 md:p-8 backdrop-blur-xl">
            <h3 className="text-2xl font-bold mb-4">Generate your content</h3>

            <div className="flex flex-col md:flex-row gap-3">
              <select value={contentType} onChange={(e) => setContentType(e.target.value)} className="w-full md:w-1/3 rounded-xl border border-white/10 bg-[#0B1020]/60 px-4 py-3 text-white placeholder-white/40">
                <option>Social Media Post</option>
                <option>Email</option>
                <option>Blog Article</option>
                <option>Ad Copy</option>
                <option>Caption</option>
              </select>

              <input
                type="text"
                placeholder="Enter your prompt here..."
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                className="w-full rounded-xl border border-white/10 bg-[#0B1020]/60 px-4 py-3 text-white placeholder-white/40"
              />

              <button
                onClick={handleGenerate}
                disabled={loading}
                className="rounded-xl bg-white px-5 py-3 font-semibold text-slate-900 shadow-lg shadow-white/10 hover:-translate-y-0.5 transition disabled:opacity-50"
              >
                {loading ? "Generating..." : "Generate"}
              </button>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
}
