import "./Home.css";

function Home() {
  return (
    <section className="home bg-gradient-to-r from-blue-600 to-purple-600 text-white min-h-screen flex items-center">
      <div className="container mx-auto flex flex-col md:flex-row items-center px-6 md:px-20">
        
        {/* Left side: Text */}
        <div className="text-left md:w-1/2 space-y-6">
          <h1 className="text-4xl md:text-5xl font-bold leading-tight">
            ðŸš€ AI Content Generator
          </h1>
          <p className="text-lg md:text-xl text-gray-200">
            Generate high-quality content for your blog, social media, or ads in seconds using the power of AI.
          </p>
          <button className="bg-white text-blue-600 font-bold py-3 px-6 rounded-xl shadow-lg hover:shadow-xl transition">
            Try Now
          </button>
        </div>

        {/* Right side: Image */}
        <div className="md:w-1/2 mt-10 md:mt-0 flex justify-center">
          <img 
            src="https://images.unsplash.com/photo-1581091012184-ef34d9e2c0be?auto=format&fit=crop&w=600&q=80" 
            alt="AI illustration" 
            className="rounded-2xl shadow-2xl max-w-full"
          />
        </div>

      </div>
    </section>
  );
}

export default Home;
