import { useState } from "react";

function HomeSection() {
  const [prompt, setPrompt] = useState("");
  const [result, setResult] = useState("");

  const handleGenerate = () => {
    // For now, fake AI response. Later, connect your Flask backend.
    setResult(`Generated content for: "${prompt}"`);
  };

  return (
    <section id="home" className="bg-gradient-to-r from-blue-600 to-purple-600 text-white min-h-screen flex flex-col justify-center items-center pt-20 px-6">
      
      {/* Hero Text */}
      <div className="text-center max-w-2xl mb-10">
        <h1 className="text-4xl md:text-5xl font-bold mb-4">ðŸš€ AI Content Generator</h1>
        <p className="text-lg md:text-xl text-gray-200 mb-6">
          Generate high-quality content for social media, blogs, or ads in seconds using AI.
        </p>
      </div>

      {/* Input + Button */}
      <div className="flex flex-col md:flex-row gap-4 w-full max-w-xl">
        <input 
          type="text"
          placeholder="Enter your content idea..."
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          className="flex-1 px-4 py-3 rounded-xl text-gray-800"
        />
        <button 
          onClick={handleGenerate}
          className="bg-white text-blue-600 font-bold py-3 px-6 rounded-xl shadow-lg hover:shadow-xl transition"
        >
          Generate
        </button>
      </div>

      {/* Result */}
      {result && (
        <div className="mt-8 bg-white text-gray-800 p-6 rounded-2xl shadow-xl max-w-xl text-center">
          {result}
        </div>
      )}

      {/* Hero Image */}
      <div className="mt-12">
        <img 
          src="https://images.unsplash.com/photo-1581091012184-ef34d9e2c0be?auto=format&fit=crop&w=600&q=80" 
          alt="AI illustration" 
          className="rounded-2xl shadow-2xl max-w-full"
        />
      </div>
    </section>
  );
}

export default HomeSection;
