import { useState, useEffect } from "react";
import { motion } from "framer-motion";

export default function Profile() {
  const [form, setForm] = useState({ email: "", password: "" });
  const [showPass, setShowPass] = useState(false);
  const token = localStorage.getItem("token");

  useEffect(() => {
    document.title = "Profile";
    if (!token) return;
    fetch("http://127.0.0.1:8000/api/profile", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setForm({ email: data.email, password: "" }))
      .catch(() => alert("Failed to fetch profile"));
  }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://127.0.0.1:8000/api/profile", {
        method: "PUT",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ password: form.password || undefined }),
      });
      const data = await res.json();
      if (!res.ok && !data.ok) throw new Error("Update failed");
      alert("Profile updated successfully!");
      setForm({ ...form, password: "" });
    } catch (err) {
      alert(err.message);
    }
  };

  return (
    <div className="bg-[#0B1020] text-white min-h-screen">
      <section className="max-w-3xl mx-auto px-6 py-20">
        <motion.div
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="rounded-3xl border border-white/10 bg-white/5 p-8 backdrop-blur-xl shadow-2xl"
        >
          <h2 className="text-3xl font-bold mb-6 text-center">Your Profile</h2>
          <form className="grid gap-6" onSubmit={handleUpdate}>
            <div>
              <label className="block text-white/70 mb-1">Email</label>
              <input
                type="email"
                name="email"
                value={form.email}
                disabled
                className="w-full rounded-xl border border-white/10 bg-[#0B1020]/60 px-4 py-3 outline-none placeholder:text-white/40"
              />
            </div>

            <div>
              <label className="block text-white/70 mb-1 flex justify-between">
                Password
                <span
                  onClick={() => setShowPass(!showPass)}
                  className="text-sm text-fuchsia-400 cursor-pointer"
                >
                  {showPass ? "Hide" : "Show"}
                </span>
              </label>
              <input
                type={showPass ? "text" : "password"}
                name="password"
                value={form.password}
                onChange={handleChange}
                className="w-full rounded-xl border border-white/10 bg-[#0B1020]/60 px-4 py-3 outline-none placeholder:text-white/40"
              />
            </div>

            <motion.button
              whileTap={{ scale: 0.98 }}
              type="submit"
              className="w-full rounded-2xl bg-white text-slate-900 px-5 py-3 font-semibold shadow-lg shadow-white/10 hover:-translate-y-0.5 transition"
            >
              Update Password
            </motion.button>
          </form>
        </motion.div>
      </section>
    </div>
  );
}
