import { useState } from "react";
import { Link } from "react-router-dom";

export default function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const isLoggedIn = !!localStorage.getItem("token"); // check login

  return (
    <nav className="bg-gray-900 text-white shadow-md">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          {/* Logo */}
          <div className="flex-shrink-0">
            <Link to="/" className="font-bold text-xl text-yellow-400">
              AI Content
            </Link>
          </div>

          {/* Desktop Menu */}
          <div className="hidden md:flex space-x-4">
            <Link to="/" className="hover:text-yellow-400">Home</Link>
            {!isLoggedIn && <Link to="/login" className="hover:text-yellow-400">Login</Link>}
            {!isLoggedIn && <Link to="/register" className="hover:text-yellow-400">Register</Link>}
            {isLoggedIn && <Link to="/dashboard" className="hover:text-yellow-400">Dashboard</Link>}
            {isLoggedIn && <Link to="/profile" className="hover:text-yellow-400">Profile</Link>}
          </div>

          {/* Mobile Hamburger */}
          <div className="md:hidden">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="focus:outline-none text-yellow-400"
            >
              <svg
                className="h-6 w-6"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                {isOpen ? (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                ) : (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                )}
              </svg>
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {isOpen && (
        <div className="md:hidden bg-gray-800">
          <Link
            to="/"
            onClick={() => setIsOpen(false)}
            className="block px-4 py-2 hover:bg-gray-700"
          >
            Home
          </Link>
          {!isLoggedIn && (
            <>
              <Link
                to="/login"
                onClick={() => setIsOpen(false)}
                className="block px-4 py-2 hover:bg-gray-700"
              >
                Login
              </Link>
              <Link
                to="/register"
                onClick={() => setIsOpen(false)}
                className="block px-4 py-2 hover:bg-gray-700"
              >
                Register
              </Link>
            </>
          )}
          {isLoggedIn && (
            <>
              <Link
                to="/dashboard"
                onClick={() => setIsOpen(false)}
                className="block px-4 py-2 hover:bg-gray-700"
              >
                Dashboard
              </Link>
              <Link
                to="/profile"
                onClick={() => setIsOpen(false)}
                className="block px-4 py-2 hover:bg-gray-700"
              >
                Profile
              </Link>
            </>
          )}
        </div>
      )}
    </nav>
  );
}
