import { motion } from "framer-motion";
import { useState } from "react";

export default function Dashboard() {
  const [contentType, setContentType] = useState("Social Media Post");
  const [prompt, setPrompt] = useState("");
  const [generatedContent, setGeneratedContent] = useState({});
  const [loading, setLoading] = useState(false);

  const handleGenerate = async () => {
    if (!prompt) return alert("Enter a prompt first!");
    setLoading(true);
    try {
      const res = await fetch("http://localhost:8000/api/generate_fast", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt, contentType }),
      });
      const data = await res.json();
      setGeneratedContent(prev => ({ ...prev, [contentType]: data.answer || "No content returned" }));
    } catch (err) {
      setGeneratedContent(prev => ({ ...prev, [contentType]: "Failed: " + err.message }));
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = (content) => {
    navigator.clipboard.writeText(content);
    alert("Content copied!");
  };

  const types = ["Social Media Post", "Email", "Blog Article", "Ad Copy", "Caption"];

  return (
    <div className="bg-[#0B1020] text-white min-h-screen p-6">
      <h1 className="text-4xl font-bold mb-6">AI Content Generator</h1>

      <div className="mb-4 flex gap-3 flex-wrap">
        <select
          value={contentType}
          onChange={(e) => setContentType(e.target.value)}
          className="p-2 rounded bg-[#1B1F3B]"
        >
          {types.map(t => <option key={t}>{t}</option>)}
        </select>

        <input
          type="text"
          placeholder="Enter your prompt..."
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          className="flex-1 p-2 rounded bg-[#1B1F3B]"
        />

        <button
          onClick={handleGenerate}
          disabled={loading}
          className="bg-white text-black px-4 py-2 rounded"
        >
          {loading ? "Generating..." : "Generate"}
        </button>
      </div>

      {types.map(t => generatedContent[t] && (
        <div key={t} className="bg-[#1B1F3B] p-4 rounded mb-3">
          <h2 className="font-bold mb-2">{t}</h2>
          <p className="whitespace-pre-wrap">{generatedContent[t]}</p>
          <button
            onClick={() => handleCopy(generatedContent[t])}
            className="mt-2 bg-purple-500 px-3 py-1 rounded hover:bg-purple-600"
          >
            Copy
          </button>
        </div>
      ))}
    </div>
  );
}
