import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await fetch("http://127.0.0.1:8000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      if (!res.ok) {
        const errData = await res.json();
        throw new Error(errData.detail || "Login failed");
      }

      const data = await res.json();
      localStorage.setItem("token", data.access_token);
      setLoading(false);
      navigate("/dashboard"); // redirect after login
    } catch (err) {
      setLoading(false);
      alert(err.message);
    }
  };

  return (
    <div className="bg-[#0B1020] text-white min-h-screen flex items-center justify-center">
      <motion.form
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        onSubmit={handleLogin}
        className="bg-white/5 backdrop-blur-xl rounded-3xl p-8 shadow-2xl w-full max-w-md"
      >
        <h2 className="text-3xl font-bold mb-6 text-center text-white">Login</h2>

        <div className="mb-4">
          <label className="block text-white/70 mb-1">Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full rounded-xl border border-white/10 bg-[#0B1020]/60 px-4 py-3 outline-none placeholder:text-white/40"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-white/70 mb-1">Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full rounded-xl border border-white/10 bg-[#0B1020]/60 px-4 py-3 outline-none placeholder:text-white/40"
            required
          />
        </div>

        <motion.button
          whileTap={{ scale: 0.98 }}
          type="submit"
          className="w-full rounded-2xl bg-white text-slate-900 px-5 py-3 font-semibold shadow-lg shadow-white/10 hover:-translate-y-0.5 transition"
        >
          {loading ? "Logging in..." : "Login"}
        </motion.button>
      </motion.form>
    </div>
  );
}
