import { useState } from "react";

export default function Dashboard() {
  const [input, setInput] = useState("");
  const [contentType, setContentType] = useState("Social Media Post");
  const [generatedContent, setGeneratedContent] = useState("");

  const handleGenerate = () => {
    if (!input) return alert("Enter a prompt first!");
    // Later: call FastAPI to generate content
    setGeneratedContent(`Generated ${contentType} for: ${input}`);
  };

  return (
    <section className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-4xl font-bold text-center text-gray-800 mb-8">
        AI Content Generator Dashboard
      </h1>

      <div className="max-w-4xl mx-auto bg-white rounded-3xl shadow-2xl p-8 space-y-6">
        {/* Input Section */}
        <div className="flex flex-col md:flex-row md:items-center md:space-x-4 space-y-4 md:space-y-0">
          <input
            type="text"
            placeholder="Enter your prompt..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            className="flex-1 px-5 py-3 border rounded-xl focus:outline-none focus:ring-2 focus:ring-yellow-400"
          />

          <select
            value={contentType}
            onChange={(e) => setContentType(e.target.value)}
            className="px-5 py-3 border rounded-xl focus:outline-none focus:ring-2 focus:ring-yellow-400"
          >
            <option>Social Media Post</option>
            <option>Email</option>
            <option>Blog Article</option>
            <option>Ad Copy</option>
            <option>Caption</option>
          </select>

          <button
            onClick={handleGenerate}
            className="px-6 py-3 bg-yellow-400 hover:bg-yellow-500 text-gray-900 font-bold rounded-xl transition"
          >
            Generate
          </button>
        </div>

        {/* Output Section */}
        <div className="mt-6 bg-gray-50 p-6 rounded-xl border border-gray-200 min-h-[200px]">
          {generatedContent ? (
            <p className="text-gray-800 whitespace-pre-line">{generatedContent}</p>
          ) : (
            <p className="text-gray-400 text-center">Your generated content will appear here...</p>
          )}
        </div>
      </div>

      {/* Quick tips or examples */}
      <div className="max-w-4xl mx-auto mt-8 bg-gradient-to-r from-yellow-400 via-pink-500 to-purple-500 text-white p-6 rounded-3xl shadow-lg">
        <h2 className="text-2xl font-bold mb-4">Tips for Better Prompts:</h2>
        <ul className="list-disc list-inside space-y-2">
          <li>Be specific about your audience and style.</li>
          <li>Mention the platform (Instagram, LinkedIn, Email...).</li>
          <li>Include tone (funny, professional, friendly, etc.).</li>
        </ul>
      </div>
    </section>
  );
}
