import { useState } from "react";
import { motion } from "framer-motion";

/* -------------------- tiny helpers -------------------- */
const fadeUp = {
  hidden: { opacity: 0, y: 24 },
  show: { opacity: 1, y: 0, transition: { duration: 0.6 } },
};
const stagger = { show: { transition: { staggerChildren: 0.12 } } };

export default function Dashboard() {
  const [prompt, setPrompt] = useState("");
  const [type, setType] = useState("Social Media Post");
  const [result, setResult] = useState("");

  const handleGenerate = () => {
    if (!prompt) return alert("Enter a prompt first!");
    // TODO: Replace with FastAPI call
    setResult(`Generated ${type} for: ${prompt}`);
  };

  return (
    <div className="bg-[#0B1020] min-h-screen text-white">
      {/* ===== HERO ===== */}
      <section className="relative overflow-hidden">
        {/* gradient glow */}
        <div aria-hidden className="pointer-events-none absolute inset-0">
          <div className="absolute -top-40 left-1/2 h-[600px] w-[600px] -translate-x-1/2 rounded-full bg-purple-600/30 blur-[120px]" />
          <div className="absolute top-40 right-1/4 h-[420px] w-[420px] rounded-full bg-blue-500/20 blur-[100px]" />
        </div>

        <div className="max-w-7xl mx-auto px-6 pt-28 pb-20 md:pt-36">
          <motion.div
            variants={stagger}
            initial="hidden"
            whileInView="show"
            viewport={{ once: true, amount: 0.3 }}
            className="grid md:grid-cols-2 gap-10 items-center"
          >
            {/* Input / Generate section */}
            <motion.div variants={fadeUp} className="space-y-6">
              <h1 className="text-4xl md:text-6xl font-extrabold">
                Generate <span className="bg-gradient-to-r from-fuchsia-400 via-sky-300 to-violet-300 bg-clip-text text-transparent">AI Content</span> Instantly
              </h1>
              <p className="text-white/70 md:text-lg">
                Create posts, emails, blogs, captions, ads — tailored for Instagram, X/Twitter, LinkedIn, TikTok, and more.
              </p>

              <div className="flex flex-col sm:flex-row gap-3">
                <input
                  type="text"
                  value={prompt}
                  onChange={(e) => setPrompt(e.target.value)}
                  placeholder="Enter your idea or prompt"
                  className="flex-1 rounded-xl px-4 py-3 bg-white/5 border border-white/10 placeholder:text-white/40 focus:outline-none focus:ring-2 focus:ring-yellow-400"
                />
            
                <select className="w-full rounded-xl bg-white/5 border border-white/10 px-4 py-3 text-white placeholder:text-white/50 focus:outline-none focus:ring-2 focus:ring-fuchsia-400">
  <option value="social">Social Media Post</option>
  <option value="email">Email</option>
  <option value="blog">Blog Article</option>
  <option value="ad">Ad Copy</option>
  <option value="caption">Caption</option>
</select>

                <button
                  onClick={handleGenerate}
                  className="rounded-xl bg-yellow-400 text-gray-900 font-semibold px-5 py-3 shadow-lg shadow-white/10 hover:bg-yellow-500 transition"
                >
                  Generate
                </button>
              </div>

              {/* Result */}
              <div className="mt-6 bg-[#0C1226]/60 p-6 rounded-3xl border border-white/10 backdrop-blur-xl min-h-[200px]">
                {result ? (
                  <p className="text-white/80">{result}</p>
                ) : (
                  <p className="text-white/50 text-center">Your generated content will appear here...</p>
                )}
              </div>
            </motion.div>

            {/* Mock preview / floating orb */}
            <motion.div variants={fadeUp} className="relative">
              <div className="rounded-3xl border border-white/10 bg-gradient-to-tr from-white/10 to-white/5 p-5 backdrop-blur-xl shadow-2xl">
                <p className="text-white/60 text-center">Live preview of generated content will appear here.</p>
              </div>
              <motion.div
                initial={{ y: -10 }}
                animate={{ y: 10 }}
                transition={{ repeat: Infinity, repeatType: "reverse", duration: 2.2, ease: "easeInOut" }}
                className="pointer-events-none absolute -right-6 -top-6 h-20 w-20 rounded-full bg-fuchsia-500/40 blur-2xl"
              />
            </motion.div>
          </motion.div>
        </div>
      </section>

      {/* ===== FEATURES ===== */}
      <section className="border-t border-white/10 bg-[#0C1226]">
        <div className="max-w-7xl mx-auto px-6 py-20">
          <h2 className="text-3xl md:text-4xl font-bold text-center mb-12">Features</h2>
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {[
              { title: "Fast Generation", desc: "Generate posts, blogs, ads in seconds", icon: "⚡" },
              { title: "Platform-Specific", desc: "Optimized for Instagram, X/Twitter, LinkedIn, TikTok", icon: "✨" },
              { title: "Custom Tone", desc: "Casual, professional, playful, or expert styles", icon: "🎙️" },
            ].map((f, i) => (
              <motion.div key={i} variants={fadeUp} className="rounded-3xl border border-white/10 bg-white/5 p-6 backdrop-blur-xl">
                <div className="mb-3 text-2xl">{f.icon}</div>
                <h3 className="text-xl font-semibold">{f.title}</h3>
                <p className="text-white/70 mt-2">{f.desc}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
}
